DROP TABLE IF EXISTS `zoom_rule`;
CREATE TABLE `zoom_rule` (
  `id` bigint(16) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `policy_id` bigint(16) NOT NULL COMMENT '策略外键',
  `rule_content` varchar(2048) NOT NULL DEFAULT '',
  `name` varchar(256) NOT NULL,
  `rule_no` varchar(16) DEFAULT NULL,
  `status` tinyint(4) NOT NULL COMMENT '规则状态  1:拟草 2:生效 3: 废弃',
  `scene_no` varchar(6) NOT NULL COMMENT '场景号 redundant 字段',
  `score` decimal(12,2) NOT NULL COMMENT '规则权重分数, 只有policy_pattern == 2 是才有效   min,max 形式',
  `decision_code` int(11) DEFAULT NULL COMMENT '操作结果 只有关联的策略的 policy_pattern == 1 是才有效, 0 通过 1 人工审核 2 拒绝  ',
  `action_code` varchar(32) DEFAULT NULL COMMENT '指令操作  只有关联的策略的 policy_pattern == 1 是才有效,  自定义',
  `description` varchar(256) DEFAULT NULL COMMENT '描述',
  `weight_value` int(11) NOT NULL COMMENT '规则执行顺序',
  `rule_content_json` varchar(2048) DEFAULT NULL,
  `created_date` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modified_date` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后一次更新时间',
  `rule_mode` tinyint(4) DEFAULT '0' COMMENT '规则模式: 0->简单规则,1->复杂规则',
  PRIMARY KEY (`id`),
  KEY `fk_policy_rule` (`policy_id`),
  CONSTRAINT `fk_policy_rule` FOREIGN KEY (`policy_id`) REFERENCES `zoom_policy` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=257 DEFAULT CHARSET=utf8 COMMENT='规则';

